import React from"react";import ReactDOM from"react-dom";import{mount,shallow}from"enzyme";import{Avatar}from"../../index";describe("Test Avatar",function(){it("renders without crashing",function(){var a=document.createElement("div");ReactDOM.render(React.createElement(Avatar,null),a)}),it("renders correct elements",function(){var a=shallow(React.createElement(Avatar,{profileImage:"path/to/image"}));expect(a.find("Image")).toHaveLength(1),expect(a.find("Icon")).toHaveLength(1)}),it("renders a profile picture if profileImage is defined",function(){var a=mount(React.createElement(Avatar,{profileImage:"path/to/image"}));expect(a.find("Image").props().src).toEqual("path/to/image")}),it("renders a faker profile picture if profileImage is not defined",function(){var a=mount(React.createElement(Avatar,null));expect(a.find("Image").props().src).toContain("https://s3.amazonaws.com/uifaces/faces/")})});