import moment from"moment";import{utils}from"../index";describe("getTurnAroundTimeDetails",function(){it("given a target of 5 days and a 1 day TAT, it should be green, display -4, and value 5",function(){var a=utils.getTurnAroundTimeDetails(5,"1987-12-18","1987-12-19");expect(a).toEqual({label:-4,color:"green",value:5})}),it("given a target of 5 days and signout on same day, it should be green, display -5, and value 5",function(){var a=utils.getTurnAroundTimeDetails(5,"1987-12-18","1987-12-18");expect(a).toEqual({label:-5,color:"green",value:5})}),it("given a target of 5 days and signout 2 days late, it should be red, display 2, and value 5",function(){var a=utils.getTurnAroundTimeDetails(5,"1987-12-18","1987-12-25");expect(a).toEqual({label:2,color:"red",value:5})}),it("if it no signout date, but now() = start date with a target of 5 days, it should display -5 and be green",function(){var a=moment("1987-12-18");Date.now=jest.fn(function(){return a});var b=utils.getTurnAroundTimeDetails(5,"1987-12-18");expect(b).toEqual({label:-5,color:"green",value:0})}),it("if it no signout date, but now() = 2 days passed target, it should display 2 and be red",function(){var a=moment("1987-12-25");Date.now=jest.fn(function(){return a});var b=utils.getTurnAroundTimeDetails(5,"1987-12-18");expect(b).toEqual({label:2,color:"red",value:7})}),it("if it no signout date, but now() = 2 days before target, it should display -2 and be yellow",function(){var a=moment("1987-12-21");Date.now=jest.fn(function(){return a});var b=utils.getTurnAroundTimeDetails(5,"1987-12-18");expect(b).toEqual({label:-2,color:"yellow",value:3})})});